import { Component } from '@angular/core';
import { Menu } from './interfaces/menu';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'ProyectoAngular16';
  public menu: Menu[] = [
    {
      title: 'listado',
      route: 'listado'
    },
    {
      title: 'crear',
      route: 'crear'
    }
  ]
}
<section class="w-full flex flex-row">
  <aside id="default-sidebar" class="w-72  h-screen" aria-label="Sidebar">
      <div class="h-full px-3 py-4  bg-gray-50 dark:bg-gray-800">
          <h2 class="text-3xl font-bold tracking-tight text-white">CRUD Empleados</h2>
         <ul class="space-y-2 font-medium">

            <li *ngFor="let item of menu">
              <a routerLink="{{item.route}}" class="flex cursor-pointer items-center p-2 text-gray-900 rounded-lg dark:text-white hover:bg-gray-100 dark:hover:bg-gray-700 group">                  
                <span class="ms-3">
                  {{item.title}}
                </span>
              </a>
            </li>        
         </ul>
      </div>
   </aside>

   <div class="p-4">
      <router-outlet></router-outlet>
   </div>
</section>
