import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { catchError, map, of } from 'rxjs';
import { Employee } from 'src/app/interfaces/employee';
import { CrudService } from 'src/app/service/crud.service';

@Component({
  selector: 'app-listado',
  templateUrl: './listado.component.html',
  styleUrls: ['./listado.component.css']
})
export class ListadoComponent {
  public listadoEmpleados: Employee[] = [];


  constructor(private service: CrudService, private router: Router){

  }

  ngOnInit(): void {
    this.devolverEmpleados();
  }

  async devolverEmpleados(){
     this.service.buscarEmpleados().subscribe(resp => {
      this.listadoEmpleados = resp;
    });
  }

  async eliminarEmpleado(id: any){
    this.service.eliminarEmpleado(id).subscribe(response => {
      console.log(response );
    },(error) => {
      console.log(error);
    });
  }

  actualizarEmpleado(id: any){
    this.router.navigate(['/actualizar/' , id]);
    console.log("click");
  }
}
<div class="container w-full mx-auto">
    <h1 class="text-4xl font-bold mb-4">Listado Empleados</h1>


    <table *ngIf="listadoEmpleados.length > 0; else noHayEmpleados" class="w-full text-sm text-left rtl:text-right text-gray-500 dark:text-gray-400">
        <thead class="text-xs text-gray-700 uppercase bg-gray-100 dark:bg-gray-700 dark:text-gray-400">
            <tr>
                <th scope="col" class="px-6 py-3">
                   Id
                </th>
                <th scope="col" class="px-6 py-3">
                    Nombre
                </th>
                <th scope="col" class="px-6 py-3">
                    Apellidos
                </th>
                <th scope="col" class="px-6 py-3">
                  Correo
                </th>
                <th scope="col" class="px-6 py-3">
                  Acciones
                </th>
            </tr>
        </thead>
        <tbody *ngFor="let empleado of listadoEmpleados">

            <tr class="bg-white dark:bg-gray-800">
              <th scope="row" class="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                  {{empleado.id}}
              </th>
              <td class="px-6 py-4 ">
                {{empleado.firstName}}
              </td>
              <td class="px-6 py-4">
                  {{empleado.lastName}}
              </td>
              <td class="px-6 py-4">
                {{empleado.email}}
              </td>
              <td class="px-6 py-4">
                <button class="px-4 py-2 bg-red-500 text-white rounded-md mr-2" (click)="eliminarEmpleado(empleado.id)">Eliminar</button>
                <button class="px-4 py-2 bg-blue-500 text-white rounded-md" (click)="actualizarEmpleado(empleado.id)">Actualizar</button>
              </td>
            </tr>     
        </tbody>
    </table>
    <ng-template #noHayEmpleados>
        <h3 class="text-2xl font-bold mb-4">Todavía no tienes empleados</h3>
    </ng-template>
  </div>
