import { Injectable } from '@angular/core';
import { Rutina } from '../interfaces/rutina';
import { Observable } from 'rxjs';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class CrudService {

  private ApiURL = "http://localhost:9000/api/rutina";

  constructor(private http: HttpClient) { 
  }

  buscarEmpleados(): Observable<Rutina[]> {
    return this.http.get<Rutina[]>(this.ApiURL);
  }


  crearEmpleado(empleado: Rutina): Observable<Rutina>{
    return this.http.post<Rutina>(this.ApiURL, empleado);
  }

  obtenerEmpleadoPorID(id: number): Observable<Rutina>{
    return this.http.get<Rutina>(`${this.ApiURL}/${id}`);
  }

  actualizarEmpleado(id:number, empleado: Rutina): Observable<Rutina>{
    return this.http.put<Rutina>(`${this.ApiURL}/${id}`, empleado);
  }

  eliminarEmpleado(id:number){
    return this.http.delete(`${this.ApiURL}/${id}`);
  }
}
