import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { User } from 'src/app/auth/interfaces/user';
import { Rutina } from 'src/app/interfaces/rutina';
import { CrudRutinaService } from 'src/app/service/crudRutina.service';

@Component({
  selector: 'app-actualizar',
  templateUrl: './actualizar.component.html',
  styleUrls: ['./actualizar.component.css']
})
export class ActualizarComponent implements OnInit {
  public miFormularioActualizar: FormGroup;

  public id_rutina!: number; //indica que si o si llega un valor

  formularioEnviado = false;

  private user!: User; 

  private rutina: Rutina = {
    rutina: '',
    user: this.user
  }

  //injectamos en el constructor 
  constructor(private fb: FormBuilder, private service: CrudRutinaService, private router: Router, private route: ActivatedRoute) { 
    this.miFormularioActualizar = this.fb.group({
      rutina: [this.rutina.rutina, [Validators.required]],
    })
  }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.id_rutina = Number(params.get('id')) // Convierte el parámetro a número
      this.service.obtenerRutinaPorID(this.id_rutina).subscribe(resp => {
        console.log(resp);
        this.miFormularioActualizar.setValue({
          rutina: resp.rutina
          // Otros campos del formulario si los hay
        });
        
      }, (error) => {
        console.log(error);
      });
    });


    
  }

  validacion(arg:string){
    return this.miFormularioActualizar.controls[`${arg}`].hasError('required') && this.miFormularioActualizar.controls[`${arg}`].touched;
  }

  enviar(){
    //archivo.ts
    this.formularioEnviado = true;
    if(this.miFormularioActualizar.invalid){
      //va campo por campo y toca todo el formulario
      this.miFormularioActualizar.markAllAsTouched();
      return;

    }

    //Si es correcto el formulario

    this.service.actualizarRutina(this.id_rutina,this.miFormularioActualizar.value).subscribe(response => {

      this.router.navigate(['/auth/listado-rutina']);
    },
    (error) => {
      console.log("Respuesta erronea: " +  error);
    });
  }
}
<section class="container my-40 mx-auto">

    <div class="absolute inset-x-0 top-[-10rem] -z-10 transform-gpu overflow-hidden blur-3xl sm:top-[-20rem]" aria-hidden="true">
      <div class="relative left-1/2 -z-10 aspect-[1155/678] w-[36.125rem] max-w-none -translate-x-1/2 rotate-[30deg] bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30 sm:left-[calc(50%-40rem)] sm:w-[72.1875rem]" style="clip-path: polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)"></div>
    </div>
    <div class="mx-auto max-w-2xl text-center">
      <h2 class="text-3xl font-bold tracking-tight text-gray-900 sm:text-4xl">Actualiza tu rutina.</h2>
      <p class="mt-2 text-lg leading-8 text-gray-600">Actualiza tu rutina de forma fácil.</p>
    </div>
    <form [formGroup]="miFormularioActualizar" (ngSubmit)="enviar()" action="#" method="POST" class="mx-auto mt-16 max-w-xl sm:mt-20">
      <div class="flex gap-x-8 gap-y-6 sm:grid-cols-2">
        <div class="w-96">
          <label for="rutina" class="block w-72 text-sm font-semibold leading-6 text-gray-900">Nombre de la rutina</label>
          <div class="mt-2.5">
            <input type="text" formControlName="rutina" name="rutina" id="rutina" autocomplete="given-name" class="block w-full rounded-md border-0 px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
            <div *ngIf="formularioEnviado && validacion('rutina')" class="bg-red-500 my-4 border rounded text-white p-2">
                Es necesario introducir un nombre para la rutina
            </div>
        </div>
        </div>
      <div class="mt-10">
        <button type="submit" class="block w-full rounded-md bg-indigo-600 px-3.5 py-2.5 text-center text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600">Actualizar rutina</button>
      </div>
    </div>
    </form>
</section>
