import { AuthService } from 'src/app/auth/services/auth.service';
import { JsonPipe } from '@angular/common';
import { Component, EventEmitter, OnInit } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { User } from 'src/app/auth/interfaces/user';
import { Ejercicios } from 'src/app/interfaces/ejercicios';
import { Rutina } from 'src/app/interfaces/rutina';
import { CrudEjercicioService } from 'src/app/service/crudEjercicio.service';
import { CrudRutinaService } from 'src/app/service/crudRutina.service';

@Component({
  selector: 'app-crear-ejercicio',
  templateUrl: './crear.component.html',
  styleUrls: ['./crear.component.css']
})
export class CrearEjercicioComponent implements OnInit {
  public miFormulario: FormGroup;

  public rutina_id!: number;

  public previewImagen: boolean = false;

  private user!: User;

  imagenFile!: String;

  public rutina: Rutina = {
    rutina: '',
    user: this.user
  };

  formularioEnviado = false;

  private ejercicio: Ejercicios = {
    nombre: '',
    series: 0,
    repeticiones: 0,
    imagen: '',
    rutina: this.rutina,
  }

  //injectamos en el constructor 
  constructor(private fb: FormBuilder, private service: CrudEjercicioService, private serviceAuth: AuthService,private serviceRutina: CrudRutinaService, private activatedRoute: ActivatedRoute, private route: Router) { 
    this.miFormulario = this.fb.group({
      nombre: [this.ejercicio.nombre, [Validators.required]],
      series: [this.ejercicio.series, [Validators.required,Validators.min(1)]],
      repeticiones: [this.ejercicio.repeticiones, [Validators.required, Validators.min(1)]],
      imagen: [this.ejercicio.imagen, [Validators.required]],
      rutina: [this.ejercicio.rutina, [Validators.required]],
    })
  }

  ngOnInit(): void {
    this.activatedRoute.params.subscribe(params => {
      //Cogemos el id del parametro
      const id = params['id'];
      this.rutina_id = id;

      //cojo la rutina
      this.serviceRutina.obtenerRutinaPorID(this.rutina_id).subscribe(resp => {
        
          this.rutina = resp;
          this.ejercicio.rutina = this.rutina;
        this.miFormulario.setValue(this.ejercicio);
      }, (error) => {
        console.log(error);
        //Si da error es que no existe 
        this.route.navigate(['/listado-ejercicios']);
      })

    });
  }


  validacion(arg:string){
    return this.miFormulario.controls[`${arg}`].hasError('required') && this.miFormulario.controls[`${arg}`].touched;
  }

  validacionNumeros(arg:string){
    return this.miFormulario.controls[`${arg}`].hasError('min') && this.miFormulario.controls[`${arg}`].touched;
  }

  enviar(){
    //archivo.ts
    this.formularioEnviado = true;
    if(this.miFormulario.invalid){
      //va campo por campo y toca todo el formulario
      this.miFormulario.markAllAsTouched();
      return;

    }
    //COger datos
    
    this.miFormulario.patchValue({
      imagen: this.imagenFile
    });
   this.service.crearEjercicios(this.miFormulario.value).subscribe(response => {
      console.log(this.miFormulario.value);
      this.route.navigate(['/auth/', this.rutina_id, 'listado-ejercicios']);
    },
    (error) => {
      console.log(error);
    });
  }

  onFileSelected(event: any) {
    const file = event.target.files[0];
    this.previewImagen = true;
    if (file) {
      const reader = new FileReader();

      reader.onload = (e: any) => {
        //const base64String = e.target.result.split(',')[1];
        const base64String = e.target.result;
        this.imagenFile = base64String;
        this.miFormulario.patchValue({ imagen: this.imagenFile });
        // Ahora puedes utilizar base64String como necesites, por ejemplo, enviarlo al servidor.
      };

      reader.readAsDataURL(file);
  }
  
}

  
  
  
}
<section class="container my-40 mx-auto">

        <div class="absolute inset-x-0 top-[-10rem] -z-10 transform-gpu overflow-hidden blur-3xl sm:top-[-20rem]" aria-hidden="true">
          <div class="relative left-1/2 -z-10 aspect-[1155/678] w-[36.125rem] max-w-none -translate-x-1/2 rotate-[30deg] bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30 sm:left-[calc(50%-40rem)] sm:w-[72.1875rem]" style="clip-path: polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)"></div>
        </div>
        <div class="mx-auto max-w-2xl text-center">
          <h2 class="text-3xl font-bold tracking-tight text-gray-900 sm:text-4xl">Empieza tu rutina.</h2>
          <p class="mt-2 text-lg leading-8 text-gray-600">Crea tu rutina y empieza a crear ejercicios.</p>
          <a [routerLink]="['/', rutina_id, 'listado-ejercicios']" class="bg-green-400 mt-10 block w-52 mx-auto text-white py-2 text-center px-4 rounded">Ir al listado de ejercicios</a>
        </div>
        <form [formGroup]="miFormulario" (ngSubmit)="enviar()" enctype="multipart/form-data" action="#" method="POST" class="mx-auto mt-16 max-w-xl sm:mt-20">
          <div class="flex flex-col items-center gap-x-8 gap-y-6 sm:grid-cols-2">
            <div class="w-96">
              <label for="nombre" class="block w-72 text-sm font-semibold leading-6 text-gray-900">Nombre del ejercicio</label>
              <div class="mt-2.5">
                <input type="text" formControlName="nombre" name="nombre" id="nombre" autocomplete="given-name" class="block w-full rounded-md border-0 px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
                <div *ngIf="formularioEnviado && validacion('nombre')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario introducir un nombre para el ejercicio.
                </div>
              </div>
            </div>

            <div class="w-96">
              <label for="series" class="block w-72 text-sm font-semibold leading-6 text-gray-900">Serie</label>
              <div class="mt-2.5">
                <input type="number" formControlName="series" name="series" id="series" autocomplete="given-name" class="block w-full rounded-md border-0 px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
                <div *ngIf="formularioEnviado && validacionNumeros('series')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario como mínimo poner una serie.
                </div>
              </div>
            </div>

            <div class="w-96">
              <label for="repeticiones" class="block w-72 text-sm font-semibold leading-6 text-gray-900">Repeticiones</label>
              <div class="mt-2.5">
                <input type="number" formControlName="repeticiones" name="repeticiones" id="repeticiones" autocomplete="given-name" class="block w-full rounded-md border-0 px-3.5 py-2 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
                <div *ngIf="formularioEnviado && validacionNumeros('repeticiones')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario como mínimo poner una repetición.
                </div>
              </div>
            </div>

            
            <input type="hidden" name="" formControlName="rutina" value="{{this.rutina_id}}">

            <label for="imagen">Imagen:</label>
            <input type="file" id="imagen"  (change)="onFileSelected($event)" accept="image/*" required>
            <input type="hidden" formControlName="imagen" value="{{this.imagenFile}}">
            <div *ngIf="this.previewImagen">
              <label for="imagen">Previsualizacion de la imagen:</label>
