import { Injectable } from '@angular/core';
import { Employee } from '../interfaces/employee';
import { Observable } from 'rxjs';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class CrudService {

  private ApiURL = "http://localhost:9000/api/employees";
  constructor(private http: HttpClient) { 
  }

buscarEmpleados(): Observable<Employee[]> {
  return this.http.get<Employee[]>(this.ApiURL);
}


  crearEmpleado(empleado: Employee){
    return this.http.post(this.ApiURL, empleado);
  }

  obtenerEmpleadoPorID(id: number){
    this.http.get(`${this.ApiURL}/${id}`);
  }

  actualizarEmpleado(id:number, empleado: Employee){
    this.http.put(`${this.ApiURL}/${id}`, empleado);
  }

  eliminarEmpleado(id:number): Observable<Employee>{
    return this.http.delete<Employee>(`${this.ApiURL}/${id}`);
  }
}
