import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { Rutina } from 'src/app/interfaces/rutina';
import { CrudService } from 'src/app/service/crudRutina.service';

@Component({
  selector: 'app-actualizar',
  templateUrl: './actualizar.component.html',
  styleUrls: ['./actualizar.component.css']
})
export class ActualizarComponent implements OnInit {
  public miFormularioActualizar: FormGroup;

  public id_empleado!: number; //indica que si o si llega un valor

  formularioEnviado = false;

  private empleado: Rutina = {
    rutina: ''
  }

  //injectamos en el constructor 
  constructor(private fb: FormBuilder, private service: CrudService, private router: Router, private route: ActivatedRoute) { 
    this.miFormularioActualizar = this.fb.group({
      firstName: [this.empleado.firstName, [Validators.required]],
      lastName: [this.empleado.lastName, [Validators.required]],
      email: [this.empleado.email, [Validators.required]]
    })
  }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.id_empleado = Number(params.get('id')) // Convierte el parámetro a número
      this.service.obtenerEmpleadoPorID(this.id_empleado).subscribe(resp => {
        this.miFormularioActualizar.setValue({
          firstName: resp.firstName,
          lastName: resp.lastName,
          email: resp.email,
          // Otros campos del formulario si los hay
        });

      }, (error) => {
        console.log(error);
      });
    });



  }


  emailValidator(control: AbstractControl): { [key: string]: boolean } | null {
    const value = control.value;

    // Verifica si el valor es nulo, tiene una longitud menor a 1 o no contiene '@'
    if (value === null || value.trim().length < 1 || !value.includes('@')) {
      return { 'invalidEmail': true };
    }

    // Si ha pasado todas las validaciones, retorna null
    return null;
  }

  validacion(arg:string){
    return this.miFormularioActualizar.controls[`${arg}`].hasError('required') && this.miFormularioActualizar.controls[`${arg}`].touched;
  }

  enviar(){
    //archivo.ts
    this.formularioEnviado = true;
    if(this.miFormularioActualizar.invalid){
      //va campo por campo y toca todo el formulario
      this.miFormularioActualizar.markAllAsTouched();
      return;

    }

    //Si es correcto el formulario

    this.service.actualizarEmpleado(this.id_empleado,this.miFormularioActualizar.value).subscribe(response => {

      this.router.navigate(['listado']);
    },
    (error) => {
      console.log("Respuesta erronea: " +  error);
    });
  }
}
<section class="container w-1/2 mx-auto">
    <h1 class="text-center text-3xl font-bold tracking-tight">Actualizar empleado con id: </h1>

    <form [formGroup]="miFormularioActualizar" (ngSubmit)="enviar()" class="w-96 mx-auto border rounded-sm mt-10 p-6 shadow-sm">
        <br>
        <div class="sm:col-span-4">
            <label for="nombre" class="block text-sm font-medium leading-6 text-gray-900">Nombre</label>
            <div class="mt-2">
              <input id="nombre" formControlName="firstName" required  type="text" class="block w-full rounded-md border-0 p-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
            </div>
                <div *ngIf="formularioEnviado && validacion('firstName')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario introducir el nombre.
                </div>
        </div>

        <div class="sm:col-span-4">
            <label for="apellidos" class="block mt-4 text-sm font-medium leading-6 text-gray-900">Apellidos</label>
            <div class="mt-2">
              <input id="apellidos" formControlName="lastName" required  type="text" class="block w-full rounded-md border-0 p-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
            </div>
                <div *ngIf="formularioEnviado && validacion('lastName')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario introducir el apellido.
                </div>

        </div>




        <div class="sm:col-span-4">
            <label for="email" class="block mt-4 text-sm font-medium leading-6 text-gray-900">Email address</label>
            <div class="mt-2">
              <input id="email" formControlName="email" required type="email" class="block w-full rounded-md border-0 p-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
            </div>
                <div *ngIf="formularioEnviado && validacion('email')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario introducir el email.
                </div>
        </div>



        <!-- Botón de Enviar -->
        <button type="submit" class="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 mt-5">Enviar</button>
    </form>
</section>
