import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Rutina } from 'src/app/interfaces/rutina';
import { CrudService } from 'src/app/service/crudRutina.service';

@Component({
  selector: 'app-listado',
  templateUrl: './listado.component.html',
  styleUrls: ['./listado.component.css']
})
export class ListadoComponent {
  public listadoRutinas: Rutina[] = [];


  constructor(private service: CrudService, private router: Router){

  }

  ngOnInit(): void {
    this.devolverRutinas();
  }

  async devolverRutinas(){
     this.service.buscarRutinas().subscribe(resp => {
      this.listadoRutinas = resp;
    });
  }

  async eliminarRutina(id: any){
    this.service.eliminarRutina(id).subscribe(response => {
      console.log(response );
    },(error) => {
      console.log(error);
    });
  }

  actualizarRutina(id: any){
    this.router.navigate(['/actualizar/' , id]);
    console.log("click");
  }
}
<div class="container w-full mx-auto my-40">
    <h1 class="text-4xl text-center font-bold mb-4">Listado de Rutinas</h1>

    <div class="flex gap-10" *ngIf="listadoRutinas.length > 0; else noHayEmpleados">
      <div *ngFor="let rutina of listadoRutinas" class="w-60 p-4 text-center bg-white border border-gray-200 rounded-lg shadow sm:p-8 dark:bg-gray-800 dark:border-gray-700">
        <h5 class="mb-2 text-3xl font-bold text-gray-900 dark:text-white">{{rutina.rutina}}</h5>
        <p class="mb-5 text-base text-gray-500 sm:text-lg dark:text-gray-400">Stay up to date and move work forward with Flowbite on iOS & Android. Download the app today.</p>
        <div class="items-center justify-center space-y-4 sm:flex sm:space-y-0 sm:space-x-4 rtl:space-x-reverse">
          <button class="px-4 py-2 bg-red-500 text-white rounded-md mr-2" (click)="eliminarRutina(rutina.id)">Eliminar</button>
          <button class="px-4 py-2 bg-blue-500 text-white rounded-md" (click)="actualizarRutina(rutina.id)">Actualizar</button>
        </div>
      </div>
    </div>



    <ng-template #noHayEmpleados>
        <h3 class="text-2xl font-bold mb-4">Todavía no tienes empleados</h3>
    </ng-template>
  </div>
