import { Component } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Employee } from 'src/app/interfaces/employee';
import { CrudService } from 'src/app/service/crud.service';

@Component({
  selector: 'app-actualizar',
  templateUrl: './actualizar.component.html',
  styleUrls: ['./actualizar.component.css']
})
export class ActualizarComponent {
  public miFormulario: FormGroup;

  formularioEnviado = false;

  private empleado: Employee = {
    firstName: '',
    lastName: '',
    email: ''
  }

  //injectamos en el constructor 
  constructor(private fb: FormBuilder, private service: CrudService, private route: Router) { 
    this.miFormulario = this.fb.group({
      firstName: [this.empleado.firstName, [Validators.required]],
      lastName: [this.empleado.lastName, [Validators.required]],
      email: [this.empleado.email, [Validators.required]]
    })
  }


  emailValidator(control: AbstractControl): { [key: string]: boolean } | null {
    const value = control.value;

    // Verifica si el valor es nulo, tiene una longitud menor a 1 o no contiene '@'
    if (value === null || value.trim().length < 1 || !value.includes('@')) {
      return { 'invalidEmail': true };
    }

    // Si ha pasado todas las validaciones, retorna null
    return null;
  }

  validacion(arg:string){
    return this.miFormulario.controls[`${arg}`].hasError('required') && this.miFormulario.controls[`${arg}`].touched;
  }

  enviar(){
    //archivo.ts
    this.formularioEnviado = true;
    console.log("Enviar");
    if(this.miFormulario.invalid){
      //va campo por campo y toca todo el formulario
      this.miFormulario.markAllAsTouched();
      return;

    }

    //Si es correcto el formulario

    this.service.crearEmpleado(this.miFormulario.value).subscribe(response => {

      this.route.navigate(['listado']);
    },
    (error) => {
      console.log("Respuesta erronea: " +  error);
    });
  }
}
