import { ChangeDetectorRef, Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { first } from 'rxjs';
import { Ejercicios } from 'src/app/interfaces/ejercicios';
import { Rutina } from 'src/app/interfaces/rutina';
import { CrudEjercicioService } from 'src/app/service/crudEjercicio.service';
import { CrudRutinaService } from 'src/app/service/crudRutina.service';

@Component({
  selector: 'app-listado-ejercicio',
  templateUrl: './listado.component.html',
  styleUrls: ['./listado.component.css']
})
export class ListadoEjerciciosComponent {
  public listadoEjercicios: Ejercicios[] = [];

  public id_rutina!: Number;
  public nombre_rutina!: String;

  constructor(private service: CrudEjercicioService, private serviceRutina: CrudRutinaService, private activatedRoute: ActivatedRoute, private router: Router){
  }



  ngOnInit(): void {
    this.activatedRoute.params.subscribe(params => {
      //Cogemos el id del parametro
      const id = params['id'];
      this.id_rutina = id;
      //comprobamos que existe en la BD
      this.serviceRutina.obtenerRutinaPorID(id).subscribe(resp => {
        //Si hay respuesta
        this.devolverEjercicios();
      }, (error) => {
        console.log(error);
        //Si da error es que no existe 
        this.router.navigate(['/listado-rutinas']);
      })
    });
  }

  
  
  

  async devolverEjercicios(){
     this.service.buscarEjerciciosPorRutina(this.id_rutina).subscribe(resp => {
      this.listadoEjercicios = resp;
    });
  }

  async eliminarEjercicio(id: any){
    this.service.eliminarEjercicios(id).subscribe(response => {
      console.log(response );
      this.devolverEjercicios();
    },(error) => {
      console.log(error);
    });

  }

  actualizarEjercicio(id: any){
    this.router.navigate(['/actualizar-Ejercicios/' , id]);
    console.log("click");
  }

  public conseguirImagen(imagen: any){
    // Extraer el contenido codificado de la cadena Base64
    imagen.split(';base64,').pop();

    // Decodificar la cadena Base64 usando atob
    const decodedData = atob(imagen);

    // Convertir la cadena decodificada a un arreglo de bytes
    const byteArray = new Uint8Array(decodedData.length);
    for (let i = 0; i < decodedData.length; i++) {
      byteArray[i] = decodedData.charCodeAt(i);
    }

    // Crear un Blob a partir del arreglo de bytes
    const blob = new Blob([byteArray], { type: 'image/png' });

    // Crear una URL válida para la imagen
    const imageUrl = URL.createObjectURL(blob);
    return imageUrl;
  }
}
<div class="container w-full mx-auto my-40">
    
    <div class="mt-10" *ngIf="listadoEjercicios.length > 0; else noHayEmpleados">
      <h2 class="text-3xl text-center font-bold mb-4">Rutina: {{this.nombre_rutina}}</h2>
      <h1 class="text-4xl text-center font-bold mb-10">Listado de Ejercicios</h1>

      <a [routerLink]="['/auth/', id_rutina, 'crear-ejercicio']" class="bg-green-400 block w-52 mx-auto text-white py-2 text-center px-4 rounded">Crear un nuevo ejercicio</a>
      <div class="flex gap-10 mt-10">
        <div *ngFor="let ejercicio of listadoEjercicios"  class="w-96 p-4 text-center bg-white border border-gray-200 rounded-lg shadow sm:p-8 dark:bg-gray-800 dark:border-gray-700">
          <img class="rounded w-full h-1/2 mb-5" [src]="ejercicio.imagen" alt="">
          <h5 class="mb-2 text-3xl font-bold text-gray-900 dark:text-white">{{ejercicio.nombre}}</h5>
          <p class="mb-0 text-base text-gray-500 sm:text-lg dark:text-gray-400">Series: {{ejercicio.series}}</p>
          <p class="mb-5 text-base text-gray-500 sm:text-lg dark:text-gray-400">Repeticiones: {{ejercicio.repeticiones}}</p>
          <div class="items-center justify-center space-y-4 sm:flex sm:space-y-0 sm:space-x-4 rtl:space-x-reverse">
         
            <button class="px-4 py-2 bg-red-500 text-white rounded-md " (click)="eliminarEjercicio(ejercicio.id)">Eliminar</button>
            <button class="px-4 py-2 bg-blue-500 text-white rounded-md" [routerLink]="['/auth/' + id_rutina + '/actualizar-ejercicio/' + ejercicio.id]">Actualizar</button>
          </div>
        </div>
      </div>
    </div>
  
 

    <ng-template #noHayEmpleados>
        <div class="w-96 mx-auto rounded bg-purple-600 p-5 text-white">
          <h3 class="text-2xl font-bold text-center mb-4">Todavía no tienes ejercicios en la rutina</h3>
          <a [routerLink]="['/', id_rutina, 'crear-ejercicio']" class="bg-purple-950 block w-3/4 mx-auto text-white py-2 text-center px-4 rounded">Crear un nuevo ejercicio</a>
        </div>
    </ng-template>
  </div>
