import { Component } from '@angular/core';
import { AbstractControl, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Employee } from 'src/app/interfaces/employee';

@Component({
  selector: 'app-crear',
  templateUrl: './crear.component.html',
  styleUrls: ['./crear.component.css']
})
export class CrearComponent {
  public miFormulario: FormGroup;

  formularioEnviado = false;

  private empleado: Employee = {
    firstName: '',
    lastName: '',
    email: ''
  }

  //injectamos en el constructor 
  constructor(private fb: FormBuilder) { 
    this.miFormulario = this.fb.group({

      //si pones mas de un validator hay que añadirle los corchetes
      nombre: [this.empleado.firstName, [Validators.required, Validators.minLength(3)]],
      apellidos: [this.empleado.lastName, [Validators.required,Validators.min(0)]],
      email: [this.empleado.email, [Validators.required,Validators.min(1),this.emailValidator]]
    })
  }


  emailValidator(control: AbstractControl): { [key: string]: boolean } | null {
    const value = control.value;

    // Verifica si el valor es nulo, tiene una longitud menor a 1 o no contiene '@'
    if (value === null || value.trim().length < 1 || !value.includes('@')) {
      return { 'invalidEmail': true };
    }

    // Si ha pasado todas las validaciones, retorna null
    return null;
  }

  validacion(arg:string){
    return this.miFormulario.controls[`${arg}`].hasError('required') && this.miFormulario.controls[`${arg}`].touched;
  }

  enviar(){
    //archivo.ts
    this.formularioEnviado = true;
    if(this.miFormulario.invalid){
      //va campo por campo y toca todo el formulario
      this.miFormulario.markAllAsTouched();
      return;
    }
    console.log(this.miFormulario.value);
  }
}
<section class="container w-1/2 mx-auto">
    <h1 class="text-center text-3xl font-bold tracking-tight">Crear Empleado</h1>

    <form [formGroup]="miFormulario" (ngSubmit)="enviar()" class="w-96 mx-auto border rounded-sm mt-10 p-6 shadow-sm">
        <br>
        <div class="sm:col-span-4">
            <label for="nombre" class="block text-sm font-medium leading-6 text-gray-900">Nombre</label>
            <div class="mt-2">
              <input id="nombre" fromControlName="firstName" required  type="text" class="block w-full rounded-md border-0 p-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
            </div>
                <div *ngIf="this.formularioEnviado && validacion('firstName')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario introducir el email.
                </div>
        </div>

        <div class="sm:col-span-4">
            <label for="apellidos" class="block text-sm font-medium leading-6 text-gray-900">Apellidos</label>
            <div class="mt-2">
              <input id="apellidos" fromControlName="lastName" required  type="text" class="block w-full rounded-md border-0 p-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
            </div>
                <div *ngIf="this.formularioEnviado && validacion('lastName')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario introducir el email.
                </div>

        </div>




        <div class="sm:col-span-4">
            <label for="email" class="block text-sm font-medium leading-6 text-gray-900">Email address</label>
            <div class="mt-2">
              <input id="email" fromControlName="email" required type="email" class="block w-full rounded-md border-0 p-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6">
            </div>
                <div *ngIf="this.formularioEnviado && validacion('email')" class="bg-red-500 my-4 border rounded text-white p-2">
                    Es necesario introducir el email.
                </div>
        </div>



        <!-- Botón de Enviar -->
        <button type="submit" class="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600 mt-5">Enviar</button>
    </form>
</section>
